import React, { useState } from "react";
import Web3 from "web3";
import contractABI from "./abi/PromptABI.json"; // Gantilah dengan ABI yang benar

const contractAddress = "0x0831b0B193FB2b771566c30bc65BAD8c9E62afa3"; // Gantilah dengan alamat kontrak Prompt
const modelId = 11;

const App = () => {
  const [prompt, setPrompt] = useState("");
  const [web3, setWeb3] = useState(null);
  const [contract, setContract] = useState(null);
  const [account, setAccount] = useState(null);
  const [requestId, setRequestId] = useState(null);

  const initWeb3 = async () => {
    if (window.ethereum) {
      const web3Instance = new Web3(window.ethereum);
      await window.ethereum.request({ method: "eth_requestAccounts" });
      const accounts = await web3Instance.eth.getAccounts();
      const contractInstance = new web3Instance.eth.Contract(contractABI, contractAddress);

      setWeb3(web3Instance);
      setContract(contractInstance);
      setAccount(accounts[0]);
    } else {
      alert("Metamask not detected!");
    }
  };

  const handleCalculateAIResult = async () => {
    if (!contract || !account) {
      alert("Please connect to Metamask first.");
      return;
    }

    try {
      const tx = await contract.methods.calculateAIResult(modelId, prompt).send({ from: account, value: web3.utils.toWei("0.01", "ether") });
      const event = tx.events.PromptRequest;
      if (event) {
        setRequestId(event.returnValues.requestId);
        alert(`AI Calculation Requested! Request ID: ${event.returnValues.requestId}`);
      }
    } catch (error) {
      console.error(error);
      alert("Transaction failed!");
    }
  };

  const handleMintWithAIResult = async () => {
    if (!contract || !account || !requestId) {
      alert("Request ID not found. Please generate AI result first.");
      return;
    }

    try {
      const tx = await contract.methods.mintWithAIResult(requestId).send({ from: account });
      const event = tx.events.MintedWithAI;
      if (event) {
        alert(`NFT Minted! Token ID: ${event.returnValues.tokenId}`);
      }
    } catch (error) {
      console.error(error);
      alert("Minting failed!");
    }
  };

  return (
    <div>
      <h2>AI NFT Generator</h2>
      <button onClick={initWeb3}>Connect Wallet</button>
      <input
        type="text"
        placeholder="One tagline about: ..."
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
      />
      <button onClick={handleCalculateAIResult}>Generate AI Result</button>
      <button onClick={handleMintWithAIResult} disabled={!requestId}>Mint NFT</button>
    </div>
  );
};

export default App;
