const App = () => {
  const [tokens, setTokens] = useState([]);
  const web3 = new Web3(Web3.givenProvider || 'https://sepolia-rollup.arbitrum.io/rpc');
  const contract = new web3.eth.Contract(ABI, CONTRACT_ADDRESS);

  useEffect(() => {
    fetchTokens();
  }, []);

  const fetchTokens = async () => {
    try {
      const response = await fetch('https://arbitrum-sepolia.blockscout.com/api/v2/tokens/' + CONTRACT_ADDRESS + '/instances');
      const data = await response.json();

      const tokenData = await Promise.all(
        data.items.map(async (item) => {
          const tokenId = item.token_id;
          const tokenURI = await contract.methods.tokenURI(tokenId).call();
          const json = JSON.parse(atob(tokenURI.split(',')[1]));

          return {
            id: tokenId,
            title: json.title,
            description: json.description,
            image: json.image,
            prompt: json.prompt,
            aigcData: json.aigc_data,
            proofType: json.proof_type
          };
        })
      );
      setTokens(tokenData);
    } catch (error) {
      console.error('Error fetching tokens:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">AI NFT Gallery</h1>
      <div className="row">
        {tokens.map((token) => (
          <div key={token.id} className="col-md-4 mb-4">
            <div className="card h-100">
              <img src={token.image} className="card-img-top" alt={token.title} />
              <div className="card-body">
                <h5 className="card-title">{token.title}</h5>
                <p className="card-text">{token.description}</p>
                <p className="card-text"><strong>Prompt:</strong> {token.prompt}</p>
              </div>
              <div className="card-footer">
                <small className="text-muted">Proof Type: {token.proofType}</small>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
