export default function App() {
  const [tokens, setTokens] = useState([]);

  useEffect(() => {
    async function fetchTokens() {
      try {
        const response = await axios.get(`https://arbitrum-sepolia.blockscout.com/api/v2/tokens/${contractAddress}/instances`);
        const tokenIds = response.data.items.map(item => item.token_id);

        const provider = new ethers.providers.JsonRpcProvider('https://sepolia-rollup.arbitrum.io/rpc');
        const contract = new ethers.Contract(contractAddress, contractABI, provider);

        const tokenData = await Promise.all(tokenIds.map(async (id) => {
          const uri = await contract.tokenURI(id);
          const base64 = uri.split(',')[1];
          const json = JSON.parse(atob(base64));
          return { id, ...json };
        }));

        setTokens(tokenData);
      } catch (error) {
        console.error('Error fetching tokens:', error);
      }
    }
    fetchTokens();
  }, []);

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">AI Generated Content NFTs</h1>
      <div className="row">
        {tokens.map(token => (
          <div className="col-md-4 mb-4" key={token.id}>
            <div className="card h-100">
              <img src={token.image} className="card-img-top" alt={token.name} />
              <div className="card-body">
                <h5 className="card-title">{token.name}</h5>
                <p className="card-text">{token.description}</p>
                <p><strong>Prompt:</strong> {token.prompt}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
