export default function App() {
  const [tokens, setTokens] = useState([]);

  useEffect(() => {
    fetch(API_URL)
      .then(response => response.json())
      .then(data => setTokens(data.items))
      .catch(error => console.error('Error fetching tokens:', error));
  }, []);

  const getTokenData = async (tokenId) => {
    if (window.ethereum) {
      const provider = new ethers.BrowserProvider(window.ethereum);
      const contract = new ethers.Contract(CONTRACT_ADDRESS, ABI, provider);
      try {
        const tokenURI = await contract.tokenURI(tokenId);
        const base64Data = tokenURI.split(',')[1];
        const jsonData = JSON.parse(atob(base64Data));
        return jsonData;
      } catch (error) {
        console.error('Error fetching tokenURI:', error);
      }
    }
  };

  return (
    <div className="p-4 grid grid-cols-1 md:grid-cols-3 gap-4">
      {tokens.map((token) => (
        <TokenCard key={token.id} tokenId={token.id} getTokenData={getTokenData} />
      ))}
    </div>
  );
}

function TokenCard({ tokenId, getTokenData }) {
  const [tokenData, setTokenData] = useState(null);

  const handleLoad = async () => {
    const data = await getTokenData(tokenId);
    setTokenData(data);
  };

  return (
    <Card className="shadow-xl p-4 rounded-2xl">
      <CardContent>
        <h2 className="text-xl font-bold mb-2">Token ID: {tokenId}</h2>
        <Button onClick={handleLoad} className="mb-2">Load Token Data</Button>
        {tokenData && (
          <div>
            <img src={tokenData.image} alt={tokenData.name} className="rounded-xl w-full h-48 object-cover" />
            <h3 className="text-lg font-semibold mt-2">{tokenData.name}</h3>
            <p>{tokenData.description}</p>
            <p className="italic">Prompt: {tokenData.prompt}</p>
            <p className="text-sm text-gray-500">AIGC Type: {tokenData.aigc_type}</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}
